(()=>{var e=document.getElementById("burgerBtn"),n=document.getElementById("sidebar"),r=document.getElementById("overlay"),t=!1;e.addEventListener("click",()=>{t=!t,n.classList.toggle("translate-x-full",!t),r.classList.toggle("hidden",!t);let[s,a,l]=e.querySelectorAll("span");t?(s.classList.add("transform","rotate-45","-translate-y-1/2"),a.classList.add("opacity-0"),l.classList.add("transform","-rotate-45","-translate-y-1/2")):(s.classList.remove("transform","rotate-45","-translate-y-1/2"),a.classList.remove("opacity-0"),l.classList.remove("transform","-rotate-45","-translate-y-1/2"))});r.addEventListener("click",()=>e.click());})();
